mapper.insert:

   		SELECT MP_BOARD_SEQ.NEXTVAL FROM DUAL

	   INSERT INTO MP_BOARD(    BNO 
	   					      , TITLE 
	   					      , CONTENT 
	   					      , WRITER
	   					      , DELETED
  )
	                 VALUES(    #{bno}
	                 		  , #{title}
	                 		  , #{content}
	                 		  , #{writer}  
	                 		  , 'N'

 )

mapper.select:
SELECT RNUM, BNO, TITLE, CONTENT, WRITER, REGDATE, HIT, EDITDATE, EDITOR, DELETED, USERNAME FROM ( SELECT B.BNO, B.TITLE, B.CONTENT, B.WRITER, B.REGDATE, B.HIT, B.EDITDATE, B.EDITOR, B.DELETED, M.USERNAME, ROW_NUMBER() OVER(ORDER BY BNO ASC) AS RNUM FROM MP_BOARD B, MP_MEMBER M
WHERE DELETED = 'N' AND B.WRITER = M.USERID
) MP WHERE RNUM BETWEEN #{rowEnd} AND #{rowStart}
ORDER BY BNO DESC

mapper.update:
UPDATE MP_BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content},
   	   EDITDATE =	sysdate,
   	   EDITOR	=	#{editor}
 WHERE BNO = #{bno}

UPDATE MP_BOARD 
	SET DELETED = 'Y' 
WHERE BNO = #{bno}

UPDATE MP_BOARD SET
HIT = HIT+1
WHERE BNO = #{bno}
