mapper.insert:
 INSERT INTO MP_MEMBER(   USERNO 
					       , USERID 
					       , USERPASS
					       , USERNAME
					       , USERBIRTH
					       , USERNICK
					       , USEREMAIL
					       , USERTELL
					       , USERGENDER
					       )
               VALUES(    MP_MEMBER_SEQ.NEXTVAL  
              		   , #{userId}
              		   , #{userPass}
              		   , #{userName}
              		   , #{userBirth}
              		   , #{userNick}
              		   , #{userEmail}
              		   , #{userTell}
              		   , #{userGender}
              		   )

mapper.select:
SELECT USERID, USERPASS, USERNAME, USERNICK, USERTELL, USERBIRTH, USEREMAIL, USERGENDER, USEREDITDATE
  FROM MP_MEMBER
 WHERE USERID = #{userId}
   AND USERPASS = #{userPass}

mapper.delete:
DELETE FROM MP_MEMBER
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}

mapper.select:
SELECT COUNT(*) FROM MP_MEMBER
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}
